head.script:
// 조건을 key: value 형태로 리턴
scwin.createCList = function (properties) {
    return Object.entries(properties)
        .filter(([key, value]) => value !== "")  // 빈 문자열 제외
        .map(([key, value]) => `${key}="${value}"`)  // key: value 형태로 변환
        .join(' ');  // 공백으로 각 property 연결
}
