head.script:
scwin.onpageload = function () {
    $c.gcm.createCommonDatalist();

    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    let eList = $c.gcm.createEList(["onclick", "ondblclick"]);

    // comp 생성 함수 호출    
    $c.gcm.createBasicButton(grp_comp, '', eList);    
}
