head.script:
scwin.gVar = {
    "cTabIndexTarget" : "",
    "cTabIndexInput" : "",
    "cTabIndexButton" : "",
    "cTabIndexSelectBox" : "",
}



scwin.onpageload = function () {
    let wf_body_left = wf_sample.getObj("wf_body_left");
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);

    $c.gcm.createCommonDatalist();

    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    // 기존 input2 있으면 삭제
    if (typeof input2 !== 'undefined') {
        input2.remove();
    }

    // 기존 button2 있으면 삭제
    if (typeof button2 !== 'undefined') {
        button2.remove();
    }

    // 기존 selectbox2 있으면 삭제
    if (typeof selectbox2 !== 'undefined') {
        selectbox2.remove();
    }

    // 조건 설정 (target)   
    let properties = {
        tabIndex: scwin.gVar["cTabIndexTarget"],
    };

    let cList = $c.gcm.createCList(properties);      

    // 조건 설정 (input)
    let properties_input = {
        tabIndex: scwin.gVar["cTabIndexInput"],
    };

    let cList_input = $c.gcm.createCList(properties_input);    

    console.log(cList_input);

    // 조건 설정 (button)
    let properties_button = {
        tabIndex: scwin.gVar["cTabIndexButton"],
    };

    let cList_button = $c.gcm.createCList(properties_button);    

    // 조건 설정 (selectbox)
    let properties_selectbox = {
        tabIndex: scwin.gVar["cTabIndexSelectBox"],
    };

    let cList_selectbox = $c.gcm.createCList(properties_selectbox);    

    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';    
    let strStartComp = '';

    strStartComp += '<xf:input ' + strNameSpace + ' tabIndex="1" initValue="start" style="width:144px; height:21px;"></xf:input>';

    scwin.startcomp = await $p.dynamicCreate(
        "startcomp",
        "input",
        strStartComp,
        grp_comp
    );

    $c.gcm.createBasicButton(grp_comp, cList, 'ev:onfocus="scwin.target1_onfocus"')
    $c.gcm.createBasicInput(grp_comp, cList_input, 'ev:onfocus="scwin.input2_onfocus"', "input2");
    $c.gcm.createBasicButton(grp_comp, cList_button, 'ev:onfocus="scwin.button2_onfocus"', "button2");
    $c.gcm.createBasicSelectBox(grp_comp, cList_selectbox, 'ev:onfocus="scwin.selectbox2_onfocus"', "selectbox2");

    target1.setLabel("target");
}
