scwin.onpageload = function () {

};



// comp 생성
scwin.comp_init = async function () {
    grp_comp = wf_common.getObj("grp_comp");

    let strTarget = '';
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';

    strTarget += '<xf:select '+strNameSpace+' selectedindex="-1" id="" appearance="full" style="width:148px;height: 100px;" cols="" rows="">';
    strTarget += '<xf:choices>';
    strTarget += '<xf:itemset nodeset="data:dataList1">';
    strTarget += '<xf:label ref="label"></xf:label>';
    strTarget += '<xf:value ref="value"></xf:value>';
    strTarget += '</xf:itemset>';
    strTarget += '</xf:choices>';
    strTarget += '</xf:select>';

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "checkbox",
        strTarget,
        grp_comp
    );
}
