head.script:
let objParent = $p.parent();



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[common > M > unbind]<br/>컴포넌트의 특정 이벤트에서 이벤트 핸들러를 제거합니다.",
    "validation" : [
        "type 파라미터로 이벤트 핸들러를 제거할 이벤트의 이름을 문자열로 설정할 수 있어야 합니다.",
        "type 파라미터 생략 시 컴포넌트의 모든 이벤트에 등록된 핸들러 전체를 제거할 수 있어야 합니다.",
        "func 파라미터로 지정한 이벤트에서 제거할 이벤트 핸들러 함수를 설정할 수 있어야 합니다.",
        "func 파라미터 생략 시 지정한 이벤트에 등록된 핸들러 전체를 제거할 수 있어야 합니다.",        
        "func 파라미터로 undefined 설정 시 지정한 이벤트에 등록된 핸들러 전체를 제거할 수 있어야 합니다.",
        "func 파라미터로 undefined 설정 시 func 파라미터값은 적용되지 않아야 합니다.",
        "익명 함수로 이벤트 핸들러를 등록한 경우 unbind() 메소드로 등록된 익명 함수를 제거할 수 없어야 합니다.",
        "사용자 정의 함수로 이벤트 핸들러를 등록한 경우 unbind() 메소드로 등록된 사용자 정의 함수를 제거할 수 있어야 합니다.",
        "이벤트 핸들러 함수가 중복으로 등록되어 있어도 해당 이벤트 핸들러 함수를 모두 제거할 수 있어야 합니다.",
        "이벤트 핸들러 함수 제거 후 남아 있는 이벤트 핸들러 함수의 순서는 유지되어야 합니다.",
    ]
}



scwin.onpageload = function () {
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};



// comp 재생성
scwin.comp_init = function () {
    // comp 생성 호출
    objParent.scwin.comp_init();
};



scwin.comm_event = function(e){
    wf_body_bottom.getObj("scwin").setReturnValue("사용자 정의 함수호출");
};



scwin.btn_bind_onclick = function (e) {
    let pType = par_type.getValue();
    let pFunc = par_func.getText();

    objParent.target1.bind(pType, eval('('+pFunc+')'));
};



scwin.btn_unbind_onclick = function (e) {
    let pType = par_type.getValue();
    let pFunc = par_func.getText();

    if (pType !== '' && pFunc !== '') {
        if (pType == 'undefined')
            pType = undefined;
        if (pFunc == 'undefined')
            pFunc = undefined;

        objParent.target1.unbind(pType, eval('('+pFunc+')'));               
    }
    else if (pType !== '' && pFunc == '') {
        if (pType == 'undefined')
            pType = undefined;
        objParent.target1.unbind(pType);
    }
    else if (pType == '' && pFunc !== '') {
        objParent.target1.unbind(undefined, eval('('+pFunc+')'));    
    }
    else {
        objParent.target1.unbind();
    }
};
