head.script:
let objParent = $p.parent();



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[common > E > onkeypress]<br/>컴포넌트 영역에서 키보드로 문자 입력 키를 누를때 발생합니다.",
    "validation" : [
        "컴포넌트 영역에서 키보드로 문자 입력 키를 누를때 이벤트가 발생하여야 합니다.",
        "기능키(F1~F12)와 같이 문자 입력이 아닌 키를 누를때는 이벤트가 발생하지 않아야 합니다.",
        "키를 누르고 있으면 이벤트가 연속적으로 발생하여야 합니다.",
        "문자 입력에 해당하는 키 입력시 onkeydown 이벤트가 발생한 후 onkeypress 이벤트가 발생하여야 합니다.",
        "자식을 가지는 컴포넌트의 경우 자식 컴포넌트에서 onkeydown 이벤트가 발생하면 부모 컴포넌트로 버블링 되어야 합니다.",
    ]
} 



scwin.onpageload = function () {
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};



// comp 생성
scwin.comp_init = async function () {
    // comp 생성 호출
    objParent.scwin.comp_init();
}



objParent.scwin.target1_onkeydown = function (e) {
    wf_body_bottom.getObj("scwin").setReturnValue("=====target1_onkeydown 이벤트 발생=====");
    wf_body_bottom.getObj("scwin").setReturnValue("e:" + e);   
}



objParent.scwin.target1_onkeypress = function (e) {
    wf_body_bottom.getObj("scwin").setReturnValue("=====target1_onkeypress 이벤트 발생=====");
    wf_body_bottom.getObj("scwin").setReturnValue("e:" + e);
}
