head.script:
let objParent = $p.parent();



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[common > M > getGenarator]<br/>컴포넌트가 Generator 컴포넌트의 자식으로 생성된 경우 부모 Generator 컴포넌트를 반환합니다.",
    "validation" : [
        "Generator 컴포넌트의 자식인 경우 부모 Generator 컴포넌트 객체를 반환하여야 합니다.",       
        "Generator 컴포넌트의 자식이 아닌 경우 최상위 WFrame 컴포넌트를 반환하여야 합니다.",
    ]
}



scwin.onpageload = function () {
    // Global DataList 생성 호출
    $c.gcm.createCommonDatalist();

    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};




// comp 재생성
scwin.comp_init = function () {
    // comp 생성 호출
    objParent.scwin.comp_init();
};



objParent.scwin.group1_onclick = function (e) {
    let pObj = this.getGenerator();

    if (pObj.getPluginName() == "generator")
       wf_body_bottom.getObj("scwin").setReturnValue("Generator component");        
    else
      wf_body_bottom.getObj("scwin").setReturnValue("Not a Generator component");
}



objParent.scwin.target1_onclick = function (e) {
    let pObj = this.getGenerator();

    if (pObj.getPluginName() == "generator")
       wf_body_bottom.getObj("scwin").setReturnValue("Generator component");        
    else
      wf_body_bottom.getObj("scwin").setReturnValue("Not a Generator component");
}
