head.script:
let objParent = $p.parent();

let pOptions = {};



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[common > M > init]<br/>컴포넌트의 자식 컴포넌트 중 입력값을 갖는 컴포넌트의 입력값을 초기화합니다.",
    "validation" : [
        "입력값을 갖는 컴포넌트는 Calendar, Checkbox, CheckCombobox, GridView, InputBox, InputCalendar, MultiSelect, Radio, SelectBox, Textarea 입니다.",
        "options(선택) 파라미터로 입력값을 초기화 할 때 적용할 조건을 객체 형태로 설정할 수 있어야 합니다.",
        "options.excludePlugin 파라미터로 초기화 대상에서 제외 할 컴포넌트의 종류를 컴포넌트명(PluginName)으로 설정할 수 있어야 합니다.",        
        "options.excludePlugin 파라미터로 초기화 대상에서 제외 할 컴포넌트의 종류를 공백(' ')을 사용하여 목록 형태로 설정할 수 있어야 합니다.",
        "options.excludeId 파라미터로 초기화 대상에서 제외 할 컴포넌트의 ID 를 설정할 수 있어야 합니다.",
        "options.excludeId 파라미터로 초기화 대상에서 제외 할 컴포넌트의 ID 를 공백(' ')을 사용하여 목록 형태로 설정할 수 있어야 합니다.",
        "options.fireEvent 파라미터로 true 설정 시 초기화 과정에서 값 변동에 따른 이벤트를 발생시켜야 합니다.",
        "options.fireEvent 파라미터로 false 설정 시 초기화 과정에서 값 변동에 따른 이벤트를 발생시키지 않아야 합니다.",
        "options.recursive 파라미터로 true 설정 시 초기화 대상 컴포넌트의 자식 컴포넌트까지 연속적으로 초기화 대상에 포함하여야 합니다.",
        "options.recursive 파라미터로 false 설정 시 초기화 대상 컴포넌트의 자식 컴포넌트까지 연속적으로 초기화 대상에 포함하지 않아야 합니다.",
        "onviewchange 이벤트는 이벤트 특성상 fireEvent 파라미터가 true 여도 발생하지 않아야 합니다.",
        "options.recursive 파라미터로 true 설정 시 options.excludeId 에 설정한 값과 ID 가 일치하는 모든 자식 컴포넌트가 초기화 되지 않아야 합니다.",
        "자식 컴포넌트가 Group, Switch 이면 recursive 파라미터 값에 관계없이 해당 컴포넌트의 자식 컴포넌트도 초기화되어야 합니다.",
        "InputBox, Spinner 컴포넌트는 initValue 속성값으로 입력값이 초기화되어야 합니다.",
    ]
} 



scwin.onpageload = function () {
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);    
};



// comp 재생성
scwin.comp_init = function () {
    // comp 생성 호출
    objParent.scwin.comp_init();
};



scwin.set_parameter_1 = function () {
    let strOptions = par_options.getValue();

    if (strOptions != "")
        pOptions = eval('(' + strOptions + ')');
    else    
        pOptions = {};
};



scwin.btn_setValue_onclick = function (e) {
    // 값 변경
    // objParent.calendar2.gotoCalendar("20250102");
    objParent.checkbox2.setValue("2");
    objParent.checkcombobox2.setValue("2");
    objParent.input2.setValue("inswave");
    objParent.inputcalendar2.setValue("20250102");
    objParent.radio2.setSelectedIndex(1);
    objParent.selectbox2.setSelectedIndex(1);
    objParent.textarea2.setValue("textarea");
    objParent.group2_textarea2.setValue("group_textarea");
    objParent.multiselect2.setValue(2);
    objParent.spinner2.setValue(10);
    objParent.case2_input1.setValue("case2_input")
};



scwin.btn_getValue_onclick = function (e) {
    // wf_body_bottom.getObj("scwin").setReturnValue(objParent.calendar2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.checkbox2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.checkcombobox2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.input2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.inputcalendar2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.radio2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.selectbox2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.textarea2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.group2_textarea2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.multiselect2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.spinner2.getValue());
    wf_body_bottom.getObj("scwin").setReturnValue(objParent.case2_input1.getValue());
};




scwin.btn_init_onclick = function (e) {
    scwin.set_parameter_1();

    objParent.target1.init(pOptions);
};



