head.script:
let objParent = $p.parent();



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[common > M > show]<br/>hide() 메소드로 숨겨져 있던 컴포넌트를 화면에 표시합니다.",
    "validation" : [
        "displayType(선택) 파라미터로 inline style 의 display 속성에 적용할 값을 문자열로 설정합니다.",
        "displayType(선택) 파라미터 생략 시 'block' 으로 설정되어야 합니다.",
        "displayType(선택) 파라미터를 '' 으로 설정 시 display 속성을 inline style 에서 삭제하여야 합니다.",
        "파라미터에 유효하지 않은 display 속성값 설정 시 none 값이 유지되어야 합니다.",
        "부모 컴포넌트를 표시하면 자식 컴포넌트도 표시되어야 합니다.",
        "컴포넌트의 inline style 에 visibility: visible 과 파라미터로 설정한 display 값이 적용되어야 합니다.",
        "컴포넌트의 aria-hidden 속성이 false 로 설정되어야 합니다.",
    ]
}



scwin.onpageload = function () {
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};




// comp 재생성
scwin.comp_init = function () {
    // comp 생성 호출
    objParent.scwin.comp_init();
};



scwin.btn_hide_onclick = function (e) {
    objParent.target1.hide();
    scwin.get_info();
};



scwin.get_info = function (e) {
    rtn = objParent.target1.render.style.display;
    wf_body_bottom.getObj("scwin").setReturnValue("target1 display : " + rtn);

    rtn = objParent.target1.render.style.visibility;
    wf_body_bottom.getObj("scwin").setReturnValue("target1 visibility : " + rtn);

    rtn = objParent.target1.render.ariaHidden;
    wf_body_bottom.getObj("scwin").setReturnValue("target1 ariaHidden : " + rtn); 
};

scwin.btn_show_onclick = function (e) {
    if (par_displayType.getValue() == '생략')
        objParent.target1.show();
    else
        objParent.target1.show(par_displayType.getValue());

    scwin.get_info();
};
