head.script:
let objParent = $p.parent();

let cTabIndexTarget = '';
let cTabIndexInput = '';
let cTabIndexButton = '';
let cTabIndexSelectBox = '';



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[common > P > tabIndex]<br/>Tab 키를 사용하여 컴포넌트 간 포커스를 이동할 때 컴포넌트의 순서를 설정합니다.",
    "validation" : [
        "Tab 키에 의한 포커스 이동 순서를 양수, 0, 음수로 설정할 수 있어야 합니다",
        "미설정 시 0 으로 적용되어야 합니다.",
        "양수로 설정된 컴포넌트 중 작은 숫자의 컴포넌트부터 포커스를 받아야 합니다.", 
        "0 으로 설정된 컴포넌트는 양수로 설정된 컴포넌트를 모두 순회한 후 포커스가 이동되어야 합니다.",        
        "음수로 설정된 컴포넌트는 포커스 이동에서 제외되어야 합니다.",
        "동일한 값을 설정한 경우 소스에 정의된 순서대로 포커스가 이동되어야 합니다 .",        
    ]
}



scwin.onpageload = function () {
    $c.gcm.createCommonDatalist();

    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};




// comp 재생성
scwin.comp_init = function () {
    // 조건 설정
    cTabIndexTarget = con_tabIndex_target.getValue();
    cTabIndexInput = con_tabIndex_input.getValue();
    cTabIndexButton = con_tabIndex_button.getValue();
    cTabIndexSelectBox = con_tabIndex_selectbox.getValue();

    // 테스트 샘플에 재생성 조건 값 전달
    objParent.scwin.gVar["cTabIndexTarget"] = cTabIndexTarget;
    objParent.scwin.gVar["cTabIndexInput"] = cTabIndexInput;
    objParent.scwin.gVar["cTabIndexButton"] = cTabIndexButton;
    objParent.scwin.gVar["cTabIndexSelectBox"] = cTabIndexSelectBox;

    // comp 생성 호출
    objParent.scwin.comp_init();
};


scwin.btn_setStartFocus_onclick = function (e) {    
    objParent.startcomp.focus();
};



objParent.scwin.target1_onfocus = function (e) {
    wf_body_bottom.getObj("scwin").setReturnValue("target1_onfocus");    
}


objParent.scwin.target1_input1_onfocus = function (e) {
    wf_body_bottom.getObj("scwin").setReturnValue("target1_input1_onfocus");    
}


objParent.scwin.input2_onfocus = function (e) {
    wf_body_bottom.getObj("scwin").setReturnValue("input2_onfocus");    
}


objParent.scwin.button2_onfocus = function (e) {
    wf_body_bottom.getObj("scwin").setReturnValue("button2_onfocus");    
}


objParent.scwin.selectbox2_onfocus = function (e) {
    wf_body_bottom.getObj("scwin").setReturnValue("selectbox2_onfocus");    
}
