head.script:
let objParent = $p.parent();

let cTooltip = "";
let cTooltipDisplay = "";
let cTooltipFormatter = "";



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[common > P > tooltipDisplay]<br/>컴포넌트의 내부 텍스트가 모두 표시되지 않을 때 툴팁의 표시 여부를 설정합니다.",
    "validation" : [        
        "tooltipFormatter 속성이 설정되면 동작하지 않아야 합니다.",
        "tooltip 속성에 의한 툴팁은 별도로 처리할 수 있어야 합니다.",
        "컴포넌트의 CSS 에 text-overflow:ellipsis 가 적용된 경우 동작하여야 합니다.",
        "true 설정 시 내부 텍스트를 툴팁으로 표시할 수 있어야 합니다.",
        "false 설정 시 내부 텍스트를 툴팁으로 표시할 수 없어야 합니다.",
        "미설정 시 내부 텍스트를 툴팁으로 표시할 수 없어야 합니다.",
    ]
} 



scwin.onpageload = function () {
    $c.gcm.createCommonDatalist();

    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};



// comp 생성
scwin.comp_init = async function () {
    // 조건 설정
    cTooltip = con_tooltip.getValue();
    cTooltipDisplay = con_tooltipDisplay.getText();
    cTooltipFormatter = con_tooltipFormatter.getValue();

    // 테스트 샘플에 재생성 조건 값 전달
    objParent.scwin.gVar["cTooltip"] = cTooltip;
    objParent.scwin.gVar["cTooltipDisplay"] = cTooltipDisplay;
    objParent.scwin.gVar["cTooltipFormatter"] = cTooltipFormatter;

    // comp 생성 호출
    objParent.scwin.comp_init();
}



objParent.scwin.fn_tooltip = function (id, text) {
    return "id - " + id + ", tooltip - " + text;   
}



objParent.scwin.target1_onclick = function (id, text) { 
    // tooltip 확인
    if(objParent.target1.render.ariaDescribedByElements) {
        wf_body_bottom.getObj("scwin").setReturnValue("tooltip : " + objParent.target1.render.ariaDescribedByElements[0].textContent);
    }        

    // tooltipDisplay 확인
    if (objParent.target1.tooltip) {
        wf_body_bottom.getObj("scwin").setReturnValue("tooltipDisplay : " + objParent.target1.tooltip.render.textContent);
    }                
}
