head.script:
scwin.gVar = {
    "cTabIndex": "",
    "cTagName": "",
}



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[group > E > onfocus]<br/>컴포넌트가 포커스를 받을 때 발생합니다.",
    "validation" : [
        "기본 생성된 group 컴포넌트는 div 태그로 적용되므로 이벤트가 발생하지 않아야 합니다.",
        "group 컴포넌트의 tabIndex 속성을 설정하여 포커스를 받을 수 있는 상태에서 이벤트가 발생하여야 합니다.",
        "group 컴포넌트의 tagname 속성을 설정하여 포커스를 받을 수 있는 상태에서 이벤트가 발생하여야 합니다.",
        "group 컴포넌트의 자식 요소로 포커스가 이동되면 group 컴포넌트의 onfocus 이벤트가 발생하지 않아야 합니다.",
    ]
} 


scwin.wf_sample_onwframeload = function () {
    let wf_body_left = wf_sample.getObj("wf_body_left");
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};



scwin.onpageload = function () {
    $c.gcm.createCommonDatalist();

    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    // 조건 설정
    let properties = {
        tabIndex: scwin.gVar["cTabIndex"],
        tagname: scwin.gVar["cTagName"],
    };

    let cList = $c.gcm.createCList(properties);

    // comp 생성 함수 호출    
    $c.gcm.createBasicGroup(grp_comp, cList, 'ev:onfocus="scwin.target1_onfocus"');    
}
