head.script:
let cTooltip = "";
let cTooltipDisplay = "";



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[group > E > ontooltiphide]<br/>group 컴포넌트에서 툴팁이 사라질 때 발생합니다.",
    "validation" : [        
        "tooltip 속성이 설정된 상태에서 툴팁이 표시되었다가 사라질 때 이벤트가 발생하여야 합니다.",
        "tooltip 속성이 설정되지 않은 상태에서는 이벤트가 발생하지 않아야 합니다.",
        "tooltipDisplay 속성에 의해 툴팁이 표시되었다가 사라질 때는 이벤트가 발생하지 않아야 합니다.",
        "group 컴포넌트에 툴팁이 표시된 상태에서 자식 요소 영역으로 마우스 커서가 이동할 때도 이벤트가 발생하여야 합니다.",
    ]
} 



scwin.onpageload = function () {
    $c.gcm.createCommonDatalist();

    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);

    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    // 조건 설정
    cTooltip = con_tooltip.getValue();
    cTooltipDisplay = con_tooltipDisplay.getText();

    let properties = {
        tooltip: cTooltip,
        tooltipDisplay: cTooltipDisplay,
    };

    let cList = $c.gcm.createCList(properties);

    // comp 생성
    let strTarget = '';
    let strInput = '';    
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';

    strTarget += '<xf:group ' + strNameSpace + ' ' + cList + ' style="display:flex;flex-direction:column;width:150px; height:200px; background-color:green;text-overflow:ellipsis;white-space:nowrap;overflow:hidden;" ev:ontooltiphide="scwin.target1_ontooltiphide" text="abcdefghijklmnopqrstuvwxyz">';
    strTarget += '</xf:group>';

    console.log(strTarget);

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "group",
        strTarget,
        grp_comp
    );

    strInput += '<xf:input ' + strNameSpace + ' id="" style="width:144px; height:21px; " initValue="input"></xf:input>';  

    scwin.input1 = await $p.dynamicCreate(
        "input1",
        "input",
        strInput,
        target1
    );    
}



scwin.target1_ontooltiphide = function (e) {
    wf_body_bottom.getObj("scwin").setReturnValue("=====target1_ontooltiphide 이벤트 발생=====");
    wf_body_bottom.getObj("scwin").setReturnValue("e:" + e);   
}
