head.script:
scwin.gVar = {
    "cStyle" : "",
    "cStyleParent" : "",
}


scwin.onpageload = function () {
    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof parent1 !== 'undefined') {
        parent1.remove();
    }

    // 조건 설정   
    let cStyle = scwin.gVar["cStyle"];
    cStyle += "width:300px; height:100px; background-color:green;" 
    let cStyleParent = scwin.gVar["cStyleParent"];


    let properties = {
        style: cStyle,
    };

    let cList = $c.gcm.createCList(properties);   

    // target 생성
    let strParent = '';
    let strTarget = '';
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';

    strParent += '<xf:group ' + strNameSpace + ' style="background-color:var(--ws-green-100); height: 400px; '+cStyleParent+'">';
    strParent += '</xf:group>';

    scwin.parent1 = await $p.dynamicCreate(
        "parent1",
        "group",
        strParent,
        grp_comp    
    );

    console.log(strParent);

    strTarget += '<xf:group ' + strNameSpace + ' ' +cList+'>';
    strTarget += '</xf:group>';

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "group",
        strTarget,
        parent1
    );

    scwin.input1 = await $p.dynamicCreate(
        "input1",
        "input",
        {
            initValue : "인스웨이브"
        },
        target1
    );
}
