head.script:
scwin.onpageload = function () {
    $c.gcm.createCommonDatalist();

    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    let strTarget = '';
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';

    strTarget += '<xf:group ' + strNameSpace + ' style="background-color:var(--ws-green-300);"></xf:group>'

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "group",
        strTarget,
        grp_comp
    );

    $c.gcm.createBasicCalendar(target1, '', 'ev:onchange="scwin.calendar2_onchange" ev:onviewchange="scwin.calendar2_onviewchange"', 'calendar2');
    $c.gcm.createBasicCheckBox(target1, '', 'ev:onchange="scwin.checkbox2_onchange" ev:onviewchange="scwin.checkbox2_onviewchange"', 'checkbox2');
    $c.gcm.createBasicCheckComboBox(target1, '', 'ev:onchange="scwin.checkcombobox2_onchange" ev:onviewchange="scwin.checkcombobox2_onviewchange"', 'checkcombobox2');
    $c.gcm.createBasicInput(target1, '', 'ev:onchange="scwin.input2_onchange" ev:onviewchange="scwin.input2_onviewchange"', 'input2');
    $c.gcm.createBasicInputCalendar(target1, '', 'ev:onchange="scwin.inputcalendar2_onchange" ev:onviewchange="scwin.inputcalendar2_onviewchange"', 'inputcalendar2');
    $c.gcm.createBasicMultiSelect(target1, '', 'ev:onchange="scwin.multiselect2_onchange" ev:onviewchange="scwin.multiselect2_onviewchange"', 'multiselect2');
    $c.gcm.createBasicRadio(target1, '', 'ev:onchange="scwin.radio2_onchange" ev:onviewchange="scwin.radio2_onviewchange"', 'radio2');
    $c.gcm.createBasicSelectBox(target1, 'ev:onchange="scwin.selectbox2_onchange" ev:onviewchange="scwin.selectbox2_onviewchange"', '', 'selectbox2');
    $c.gcm.createBasicTextArea(target1, '', 'ev:onchange="scwin.textarea2_onchange" ev:onviewchange="scwin.textarea2_onviewchange"', 'textarea2'); 
    $c.gcm.createBasicGroup(target1, '', 'ev:onchange="scwin.group2_onchange" ev:onviewchange="scwin.group2_onviewchange"', 'group2');
    $c.gcm.createBasicTextArea(group2, '', 'ev:onchange="scwin.group2_textarea2_onchange" ev:onviewchange="scwin.group2_textarea2_onviewchange"', 'group2_textarea2');
    $c.gcm.createBasicSwitch(target1, '', 'ev:onchange="scwin.switch2_onchange" ev:onviewchange="scwin.switch2_onviewchange"', 'switch2');
    $c.gcm.createBasicSpinner(target1, '', 'ev:onviewchange="scwin.spinner2_onviewchange"', 'spinner2');
    $c.gcm.createBasicGridView(target1, '', 'ev:onviewchange="scwin.gridview2_onviewchange"', 'gridview2');
}



scwin.btn_case1_onclick = function (e) {
    switch2.setSelectedIndex(0);
};



scwin.btn_case2_onclick = function (e) {
    switch2.setSelectedIndex(1);
};
