head.script:
scwin.gVar = {
    "cLocaleRef" : "",
    "cUseLocale" : "",
}



scwin.onpageload = function () {
    // comp 초기 생성
    scwin.comp_init();
};


// 테스트 설명, 유효성 값 설정
scwin.tData = {    
    "description" : "[group > P > localeRef]<br/>클라이언트 다국어가 적용되었을 때 컴포넌트에 표시할 텍스트의 Key 값을 설정합니다.",
    "validation" : [
        "컴포넌트에 표시할 텍스트의 Key 값을 문자열로 설정할 수 있어야 합니다.",
        "useLocale=true 인 경우에 동작하여야 합니다.",
        "적용된 JS 파일에 설정된 Key 가 없을 경우 'global_undefined' 가 표시되어야 합니다.",
    ]
}



scwin.wf_sample_onwframeload = function () {
    let wf_body_left = wf_sample.getObj("wf_body_left");
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    // 조건 설정   
    let properties = {
        localeRef: scwin.gVar["cLocaleRef"],
        useLocale: scwin.gVar["cUseLocale"],
    };

    let cList = $c.gcm.createCList(properties);       

    let strTarget = '';
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';    

    strTarget += '<xf:group ' + strNameSpace + ' ' + cList + ' style="width:400px; height:300px; background-color:var(--ws-green-500);"></xf:group>'

    // target 생성
    scwin.target1 = await $p.dynamicCreate(
        'target1',
        'group',
        strTarget,
        grp_comp
    );   

    console.log(strTarget);
}
