head.script:
scwin.gVar = {
    "cTooltip": "",
    "cTooltipDisplay": "",
    "cTooltipFormatter": "",
}



scwin.onpageload = function () {
    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    // 조건 설정
    let properties = {
        tooltip: scwin.gVar["cTooltip"],
        tooltipDisplay: scwin.gVar["cTooltipDisplay"],
        tooltipFormatter: scwin.gVar["cTooltipFormatter"],
    };

    let cList = $c.gcm.createCList(properties);

    // comp 생성
    let strTarget = '';
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';

    strTarget += '<xf:group ' + strNameSpace + ' ' + cList + ' style="display:flex;flex-direction:column;width:150px; height:200px; background-color:green;text-overflow:ellipsis;white-space:nowrap;overflow:hidden;" text="abcdefghijklmnopqrstuvwxyz" ev:onclick="scwin.target1_onclick">';
    strTarget += '</xf:group>';

    console.log(strTarget);

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "group",
        strTarget,
        grp_comp
    );
}
