head.script:
scwin.gVar = {
    "cTooltip": "",
    "cTooltipDisplay": "",
    "cTooltipPositionX": "",
    "cTooltipPositionY": "",
}



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[group > P > tooltipPositionX]<br/>컴포넌트에서 표시할 툴팁의 X 좌표 위치를 설정합니다.",
    "validation" : [        
        "자식 컴포넌트 영역에 마우스가 들어가면 자식 컴포넌트의 tooltipPositionX 속성값이 컴포넌트에 바로 적용되어 툴팁의 위치가 조정되어야 합니다.",    
    ]
} 



scwin.wf_sample_onwframeload = function () {
    let wf_body_left = wf_sample.getObj("wf_body_left");
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};



scwin.onpageload = function () {
    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    // 조건 설정
    let properties = {
        tooltip: scwin.gVar["cTooltip"],
        tooltipDisplay: scwin.gVar["cTooltipDisplay"],
        tooltipPositionX: scwin.gVar["cTooltipPositionX"],
        tooltipPositionY: scwin.gVar["cTooltipPositionY"],
    };

    let cList = $c.gcm.createCList(properties);

    // comp 생성
    let strTarget = '';
    let strTextBox = '';    
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';

    strTarget += '<xf:group ' + strNameSpace + ' style="display:flex;flex-direction:column;width:150px; height:200px; background-color:green;text-overflow:ellipsis;white-space:nowrap;overflow:hidden;" text="abcdefghijklmnopqrstuvwxyz" ev:onclick="scwin.target1_onclick">';
    strTarget += '</xf:group>';

    console.log(strTarget);

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "group",
        strTarget,
        grp_comp
    );

    strTextBox += '<w2:textbox ' + strNameSpace + ' ' + cList + ' label="textbox_abcdefghijklmnopqrstuvwxyz" style="width:100px; height:23px; background-color:silver;text-overflow:ellipsis;white-space:nowrap;overflow:hidden;" ev:onclick="scwin.textbox1_onclick"></w2:textbox>';

    scwin.textbox1 = await $p.dynamicCreate(
        "textbox1",
        "textbox",
        strTextBox,
        target1
    );    
}
