head.script:
scwin.gVar = {
    "cTooltip": "",
    "cTooltipDisplay": "",
    "cTooltipPositionX": "",
    "cTooltipPositionY": "",
    "cTooltipFormatter": "",
    "cTooltipLocaleRef": "",
    "cUseLocale": "",
}



// 테스트 설명, 유효성 값 설정
scwin.tData = {
    "description" : "[group > P > tooltip]<br/>컴포넌트 영역으로 마우스가 들어왔을 때 표시할 툴팁을 설정합니다.",
    "validation" : [        
        "툴팁에 표시할 텍스트를 문자열로 설정할 수 있어야 합니다.",
        "값을 설정하지 않으면 툴팁을 표시하지 않아야 합니다.",
        "tooltip 속성을 설정하지 않으면 tooltipLocaleRef, tooltipFormatter 속성은 모두 무시되어야 합니다.",
        "tooltipFormatter 속성을 설정하면 사용자 함수에 tooltip 속성값이 전달되고 함수의 반환값이 툴팁에 표시되어야 합니다.",
        "tooltip, useLocale, tooltipLocalRef 속성을 모두 설정하여 클라이언트 다국어가 적용되면 tooltip 속성값은 무시되어야 합니다.",
        "tooltip, useLocale, tooltipLocalRef 속성을 모두 설정하여 클라이언트 다국어가 적용된 상태에서 tooltipFormatter 속성을 설정하면 사용자 함수에 tooltipLocalRef 의 Value 값이 전달되고 함수의 반환값이 툴팁에 표시되어야 합니다.",
        "tooltipDisplay=true 로 설정하면 두 개의 툴팁이 별도로 표시될 수 있어야 합니다.",
        "tooltipDisplay, tooltipFormatter 속성을 모두 설정하면 tooltipDisplay 속성에 의한 툴팁은 표시되지 않아야 합니다.",        
    ]
} 



scwin.wf_sample_onwframeload = function () {
    let wf_body_left = wf_sample.getObj("wf_body_left");
    // 테스트 설명, 유효성 생성
    wf_body_left.getObj("scwin").createValidation(scwin.tData);
};



scwin.onpageload = function () {
    // comp 초기 생성
    scwin.comp_init();
};



// comp 생성
scwin.comp_init = async function () {
    // comp 렌더링 할 객체 가져오기    
    if (typeof wf_sample !== 'undefined') {
        let wf_body_sample = wf_sample.getObj("wf_body_sample");
        grp_comp = wf_body_sample.getObj("grp_comp");
    }
    else {        
        grp_comp = wf_body_sample.getObj("grp_comp"); 
    }

    // 기존 target 있으면 삭제
    if (typeof target1 !== 'undefined') {
        target1.remove();
    }

    // 조건 설정
    let properties = {
        tooltip: scwin.gVar["cTooltip"],
        tooltipDisplay: scwin.gVar["cTooltipDisplay"],
        tooltipPositionX: scwin.gVar["cTooltipPositionX"],
        tooltipPositionY: scwin.gVar["cTooltipPositionY"],
        tooltipFormatter: scwin.gVar["cTooltipFormatter"],
        tooltipLocaleRef: scwin.gVar["cTooltipLocaleRef"],
        useLocale: scwin.gVar["cUseLocale"],
    };

    let cList = $c.gcm.createCList(properties);

    // comp 생성
    let strTarget = '';
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';

    strTarget += '<xf:group ' + strNameSpace + ' ' + cList + ' style="display:flex;flex-direction:column;width:150px; height:200px; background-color:green;text-overflow:ellipsis;white-space:nowrap;overflow:hidden;" text="abcdefghijklmnopqrstuvwxyz" ev:onclick="scwin.target1_onclick">';
    strTarget += '</xf:group>';

    console.log(strTarget);

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "group",
        strTarget,
        grp_comp
    );
}
