// comp 생성
scwin.comp_init = async function () {
    grp_comp = wf_common.getObj("grp_comp");

    let strTarget = '';
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';

    strTarget += '<xf:select '+strNameSpace+' id="" appearance="minimal" style="width:150px;" ref="">';
    strTarget += '<xf:choices>';
    strTarget += '<xf:item>';
    strTarget += '<xf:label><![CDATA[현대]]></xf:label>';
    strTarget += '<xf:value><![CDATA[1]]></xf:value>';
    strTarget += '</xf:item>';
    strTarget += '<xf:item>';
    strTarget += '<xf:label><![CDATA[기아]]></xf:label>';
    strTarget += '<xf:value><![CDATA[2]]></xf:value>';
    strTarget += '</xf:item>';
    strTarget += '<xf:item>';
    strTarget += '<xf:label><![CDATA[벤츠]]></xf:label>';
    strTarget += '<xf:value><![CDATA[3]]></xf:value>';
    strTarget += '</xf:item>';
    strTarget += '</xf:choices>';
    strTarget += '</xf:select>';

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "multiselect",
        strTarget,
        grp_comp
    );
}
