// comp 생성
scwin.comp_init = async function () {
    grp_comp = wf_common.getObj("grp_comp");

    let strTarget = '';
    let strCase1 = '';
    let strCase2 = '';

    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';    

    strTarget += '<w2:tabControl '+strNameSpace+' id="" alwaysDraw="false" style="width:500px;height:200px;">';
    strTarget += '<w2:tabs disabled="false" style="height:40px;" id="tabs1" label="Tabs1"></w2:tabs>';
    strTarget += '<w2:tabs disabled="false" style="height:40px" id="tabs2" label="Tabs2"></w2:tabs>';
    strTarget += '<w2:content alwaysDraw="false" style="height:160px;" id="content1">';
    strTarget += '<w2:button style="width:80px; height:23px; " id="" label="tabs1"></w2:button>';
    strTarget += '</w2:content>';
    strTarget += '<w2:content alwaysDraw="false" style="height:160px" id="content2">';
    strTarget += '<xf:input id="" style="width:144px; height:21px; " initValue="tabs2"></xf:input>';
    strTarget += '</w2:content>';
    strTarget += '</w2:tabControl>';

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "tabControl",
        strTarget,
        grp_comp
    );
}
