// comp 생성
scwin.comp_init = async function () {
    grp_comp = wf_common.getObj("grp_comp");

    let strTarget = '';
    let strNameSpace = 'xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"';    

    strTarget += '<w2:windowContainer '+strNameSpace+' style="width:500px; height:300px; position:relative; ">';
    strTarget += '<w2:windowToolbar>';
    strTarget += '<w2:controlIconLayer>';
    strTarget += '<w2:controlIcon></w2:controlIcon>';
    strTarget += '<w2:controlIcon></w2:controlIcon>';
    strTarget += '<w2:controlIcon></w2:controlIcon>';
    strTarget += '<w2:controlIcon></w2:controlIcon>';
    strTarget += '<w2:controlIcon></w2:controlIcon>';
    strTarget += '</w2:controlIconLayer>';
    strTarget += '<w2:nameLayer></w2:nameLayer>';
    strTarget += '<w2:selectedNameLayer></w2:selectedNameLayer>';
    strTarget += '</w2:windowToolbar>';
    strTarget += '</w2:windowContainer>';

    scwin.target1 = await $p.dynamicCreate(
        "target1",
        "windowContainer",
        strTarget,
        grp_comp
    );

	var option = {
		title : "windowContainer_link_1", 
        src : "windowContainer_link_1.xml", 
        windowTitle : "TEST", 
        windowId : "test001", 
        openAction : "selectWindow",
	}
	target1.createWindow(option);	
}
